.nav {
  background-color: rgba(0, 0, 0, 0.8);
  padding: 15px;
}
.nav__arrow-down {
  display: none;
}
.nav__arrow-up {
  display: none;
}
.nav__links {
  display: flex;
  align-items: center;
  justify-content: center;
}
.nav__link {
  color: #fff;
  padding: 5px;
  margin: 0 15px;
  font: 400 1.25rem/1em "Raleway", sans-serif;
  cursor: pointer;
  position: relative;
}
.nav__link:hover.nav__link:after {
  transform: translateX(0%);
  opacity: 1;
}
.nav__link::after {
  position: absolute;
  width: 100%;
  height: 2px;
  bottom: 0;
  left: 0;
  content: "";
  background-color: #fff;
  transform: translateX(-100%);
  opacity: 0;
  transition: opacity 0.3s, transform 0.3s;
}

